package p0828;

public class Solution {

    private static final int MOD = 1000000007;

    public int uniqueLetterString(String s) {
        int[] count = new int[26];
        int unique = 0;
        int result = 0;

        int left = 0;
        while (left < s.length()) {
            for (int j = left; j < s.length(); j++) {
                int k = index(s.charAt(j));
                count[k]++;
                if (count[k] == 1) {
                    unique++;
                } else if (count[k] == 2) {
                    unique--;
                }
                result = (result + unique) % MOD;
            }
            int l = index(s.charAt(left));
            count[l]--;
            if (count[l] == 1) {
                unique++;
            } else if (count[l] == 0) {
                unique--;
            }
            result = (result + unique) % MOD;
            left++;
            if (left < s.length()) {
                for (int j = s.length() - 1; j >= left; j--) {
                    int k = index(s.charAt(j));
                    count[k]--;
                    if (count[k] == 1) {
                        unique++;
                    } else if (count[k] == 0) {
                        unique--;
                    }
                    result = (result + unique) % MOD;
                }
            }
            left++;
        }

        return result;
    }

    private int index(char c) {
        return c - 'A';
    }

    public static void main(String args[]) {
        Solution s = new Solution();
        System.out.println(s.uniqueLetterString("ABC"));
        System.out.println(s.uniqueLetterString("ABA"));
        System.out.println(s.uniqueLetterString(""));
    }
}
